function clickToggle() {
    $("button.navbar-toggler").click(function() {
        $("button.navbar-toggler").toggleClass("bg");
        $(".btn-toggle").toggleClass("hidden");
        $(".btn-toggle-x").toggleClass("hidden");
        $(".input-group-search-phone").removeClass("d-block"); // show = d-block
        $(".info-customer-phone").removeClass("d-block");
        $(".menu-customer-phone").removeClass("show-arr")
    })
}

function clickSearch() {
    $("#custom-search-input img").click(function() {
        $(".input-group-search-phone").toggleClass("d-block");
        $("button.navbar-toggler").removeClass("bg");
        $(".btn-toggle").removeClass("hidden");
        $(".btn-toggle-x").addClass("hidden");
        $("#navbar-collapse-1").removeClass("show");// thay in = show
        $(".info-customer-phone").removeClass("d-block");
        $(".menu-customer-phone").removeClass("show-arr")
    })
}

function clickRightMenu() {
    $("#menu-customer-phone").click(function() {
        $("button.navbar-toggler").removeClass("bg");
        $(".btn-toggle").removeClass("hidden");
        $(".btn-toggle-x").addClass("hidden");
        $("#navbar-collapse-1").removeClass("show");// thay in = show
        $(".input-group-search-phone").removeClass("d-block");
        $("#menu-customer-phone").toggleClass("show-arr");
        $("#info-customer-phone").toggleClass("d-block");
        var a = $(document).width();
        $("#info-customer-phone.d-block").css("width", a)
    })
}

function bottomToTop() {
    if ($("#back-to-top").length) {
        var b = 100,
            a = function() {
                var c = $(window).scrollTop();
                if (c > b) {
                    $("#back-to-top").addClass("d-block")
                } else {
                    $("#back-to-top").removeClass("d-block")
                }
            };
        a();
        $(window).on("scroll", function() {
            a()
        });
        $("#back-to-top").on("click", function(c) {
            c.preventDefault();
            $("html,body").animate({
                scrollTop: 0
            }, 700)
        })
    }
}

function blockUI() {
    $.blockUI({
        message: $("#spinner"),
        css: {
            top: ($(window).height() - 100) / 2 + "px",
            left: ($(window).width() - 100) / 2 + "px",
            width: "100px",
            display: "",
            border: "none",
            padding: "15px",
            "-webkit-border-radius": "10px",
            "-moz-border-radius": "10px",
            opacity: 0.3,
            color: "#fff"
        }
    })
}

function unblockUI() {
    $.unblockUI()
}

function showMask() {
    var a = $("body .mask");
    if (a.length) {
        a.show()
    } else {
        $("body").append('<div class="mask"></div>').show()
    }
}

function hideMask() {
    $("body .mask").remove()
}

function sidebar() {
    var c = window.navigator.userAgent;
    var b = c.indexOf("MSIE ");
    if (b > 0) {
        return parseInt(c.substring(b + 5, c.indexOf(".", b)), 10)
    }
    var a = c.indexOf("Trident/");
    if (a > 0) {
        var e = c.indexOf("rv:");
        $(".support-right-side-bar").css("right", 17);
        $("#back-to-top").css("right", 17);
        return parseInt(c.substring(e + 3, c.indexOf(".", e)), 10)
    }
    var d = c.indexOf("Edge/");
    if (d > 0) {
        return parseInt(c.substring(d + 5, c.indexOf(".", d)), 10)
    }
    return false
}

