function stickyHeader() {
    var d;
    var c = 0;
    var e = 5;
    var b = $("header").outerHeight();
    $(window).scroll(function(f) {
        d = true
    });
    setInterval(function() {
        if (d) {
            a();
            d = false
        }
    }, 250);

    function a() {
        var f = $(this).scrollTop();
        if (Math.abs(c - f) <= e) {
            return
        }
        if (f > c && f > b) {} else {
            if (f + $(window).height() < $(document).height()) {
                $("header").removeClass("nav-up").addClass("nav-down").animate(500)
            }
        }
        c = f
    }
}

function paginate() {
    $(".pager > li > a").click(function(c) {
        c.preventDefault();
        var b = $(c.target);
        var a = c.target.href;
        if ($(c.target).is("img")) {
            a = b.parent().attr("href");
            b = b.parent()
        }
        if (b !== undefined && a !== "" && a !== undefined && a.slice(-1) !== "#") {
            $.smoothScroll({
                scrollTarget: "#ajax-container"
            });
            blockUI();
            $.ajax({
                type: "GET",
                cache: false,
                url: a,
                success: function(d) {
                    $("#ajax-container").html(d);
                    window.history.pushState("", "", a);
                    paginate();
                    unblockUI();
                    onpopstate = function() {
                        var e = document.location.href;
                        if (e !== undefined && e !== "" && e !== "#" && e.slice(-1) !== "#") {
                            $.smoothScroll({
                                scrollTarget: "#ajax-container"
                            });
                            blockUI();
                            $.ajax({
                                type: "GET",
                                cache: false,
                                url: e,
                                success: function(f) {
                                    $("#ajax-container").html(f);
                                    paginate();
                                    unblockUI()
                                },
                                fail: function(f) {}
                            })
                        }
                    }
                },
                fail: function(d) {}
            })
        }
    })
}

