// code Show More/Less for CMR IQ 2 & CMR IQ2S (18/5/2022)
$(".show-more").click(function () {
    if($(".text").hasClass("show-more-height")) {
        $(this).text("Ẩn đi");
        $(this).addClass("_Less");
    } else {
        $(this).text("Xem thêm");
        $(this).removeClass("_Less");
    }

    $(".text").toggleClass("show-more-height");
});